            function moveMarker(){
				
              old_lat = +old_lat + +deltaLat;
              old_lon = +old_lon + +deltaLng;

              // alert(old_lat+":"+old_lon);
              var latlng = new google.maps.LatLng(old_lat, old_lon);          
              marker2.setPosition(latlng);
				
              if(j!=numDeltas)
              {
                    j++;
                    setTimeout(moveMarker, 10);                      
              }

            }
